};

export type NONCE_UPDATE = {
  contract_address: ADDRESS;
  nonce: FELT;
};

export type STATE_DIFF = {
  storage_diffs: CONTRACT_STORAGE_DIFF_ITEM[];
  deprecated_declared_classes: FELT[];
