export function getStarknetIdContract(chainId: StarknetChainId): string {
  switch (chainId) {
    case StarknetChainId.SN_MAIN:
      return StarknetIdContract.MAINNET;

    case StarknetChainId.SN_GOERLI:
      return StarknetIdContract.TESTNET;

    default:
